mapper.select:
select
,
from study where state = 1 and type=#{type,jdbcType=INTEGER} limit #{pageNumber,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

mapper.insert:
insert into study (id, title, time, 
  imgUrl, state, extra, 
  releaseBy, point, content,type
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{time,jdbcType=VARCHAR}, 
  #{imgurl,jdbcType=INTEGER}, #{state,jdbcType=INTEGER}, #{extra,jdbcType=VARCHAR}, 
  #{releaseby,jdbcType=INTEGER}, #{point,jdbcType=INTEGER}, #{content,jdbcType=LONGVARBINARY},#{type,jdbcType=INTEGER}
  )

mapper.update:
update study
set title = #{title,jdbcType=VARCHAR},
  time = #{time,jdbcType=VARCHAR},
  imgUrl = #{imgurl,jdbcType=INTEGER},
  state = #{state,jdbcType=INTEGER},
  extra = #{extra,jdbcType=VARCHAR},
  releaseBy = #{releaseby,jdbcType=INTEGER},
  point = #{point,jdbcType=INTEGER}
  type = #{type,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
